mapper.insert:
INSERT INTO SHOP_CART(
    ITEM_CODE
    , CART_CNT
    , MEM_ID
)VALUES(
     #{itemCode}
     , #{cartCnt}
     , #{memId}
)

mapper.select:
SELECT CART_CODE
FROM shop_cart
WHERE ITEM_CODE = #{itemCode}
AND MEM_ID = #{memId}

mapper.update:
UPDATE shop_cart
SET
    CART_CNT = CART_CNT + #{cartCnt}
    , CART_DATE = NOW()
WHERE ITEM_CODE = #{itemCode}
AND MEM_ID = #{memId}
